.clearfix:before, .clearfix:after{ display:table; content:" "; }
.clearfix:after{ clear:both; }

.float-left{
  float: left;
}
.float-right{
  float: right;
}
.align-right{
  text-align: right;
}
.align-left{
  text-align: left;
}
.align-center{
  text-align: center;
}
.m-b-30{
  margin-bottom: 30px;
}
.m-t-30{
  margin-top: 30px;
}
.display-table{
  display: table; width: 100%;
}
.display-tab-cell{
  display: table-cell; vertical-align: middle;
}

.col-3{
  width: 33.33%;
}
.col-2{
  width: 50%;
}

.show-for-tab{
  display: none;
}

/* ====================== HEADER STYLE ====================== */

.navUser-main, .header-middle-bar{
  border-bottom: 1px solid #e5e5e5;
}
.navUser{
  padding: 0;
}
.navUser-action{
  padding: 0.5rem 0.78571rem;
}
.navUser-item--cart{
  .navUser-action{
    padding-right: 0;
  }
}
.navUser-item-currency{
  .navUser-action{
    padding-left: 0;
  }
}
.header-middle-bar{
  padding: 30px 0; position: relative;
}
.header-contact{
  font-size: 18px; text-decoration: none;
  span{
    margin-left: 10px;
  }
}
.icon-call{
  width: 24px; height: 24px; display: inline-block; background: url("../img/icon-contact.png") no-repeat center center; background-size: 18px;
  vertical-align: middle;
}
.search-form{
  position: relative; width: 250px;
  .icon-search{
    position: absolute; top: 50%; right: 10px; margin-top: -12px; padding: 0; opacity: 0.8;
    &:hover{
      opacity: 1;
    }
  }
}
.form-fieldset{
  position: relative;
}
.icon-search{
  width: 24px; height: 24px; display: block; background: url("../img/icon-search.png") no-repeat center center; background-size: 18px;
  cursor: pointer;
}
.icon-close{
  width: 24px; height: 24px; display: block; background: url("../img/icon-close.png") no-repeat center center; background-size: 18px;
  cursor: pointer;
}
.icon-cart{
  width: 24px; height: 24px; display: block; background: url("../img/icon-cart.png") no-repeat center center; background-size: 18px;
  cursor: pointer; display: inline-block; vertical-align: middle;
}
.mobile-cart-icon{
  position: relative;
  .countPill{
    position: absolute; top: 9px; right: -9px;
  }
}
.mobile-search-icon{
  .icon-close{
    display: none;
  }
  &.active{
    .icon-close{
      display: inline-block; vertical-align: middle;
    }
    .icon-search{
      display: none;
    }
  }
}
#quickSearch{
  position: relative;
}
.quickSearchResults{
  position: absolute; top: 100%; right: 0; width: 100%; z-index: 20;
  .modal-close{
    display: none;
  }
  .productGrid{
    padding: 10px; border: 1px solid #cccccc; margin: 5px 0 0 0; background: #ffffff;
    -webkit-border-radius: 3px 3px 0 0; -moz-border-radius: 3px 3px 0 0; border-radius: 3px 3px 0 0;
    .product{
      float: none; width: auto; display: block; padding: 0;
      + .product{
        padding-top: 10px; margin-top: 10px; border-top: 1px solid #cccccc;
      }
    }
  }
  .card{
    margin-bottom: 0;
    &:before, &:after{ display:table; content:" "; }
    &:after{ clear:both; }
  }
  .card-figure{
    width: 80px; padding: 5px; border: 1px solid #cccccc; float: left; margin-right: 15px;
    -webkit-border-radius: 3px; -moz-border-radius: 3px; border-radius: 3px;
  }
  .card-figcaption{
    display: none;
  }
  .card-body{
    overflow: hidden; padding-top: 0;
  }
}
.view-all-btn{
  .button{
    display: block; margin-bottom: 0; -webkit-border-radius: 0 0 3px 3px; -moz-border-radius: 0 0 3px 3px; border-radius: 0 0 3px 3px;
  }
}
.card-img-container{
  &:after{
    display: none;
  }
}
.navPage-subMenu{
  left: 0; width: 100%;
}
.navPage-subMenu-horizontal{
  padding: 0 !important;
}
.navPage-subMenu-item-child{
  .navPage-subMenu-horizontal {
    border-left: 1px solid #cccccc;
    height: auto;
    margin-left: 15.35714rem;
    padding: 0 !important;
    top: 0;
    width: 16.35714rem;
  }
}
.navPages-item, .navPage-subMenu-item-child{
  &:hover{
    > .navPages-action{
      background: #e5e5e5;
    }
    > .navPage-subMenu{
      display: block;
    }
    > .navPage-subMenu-level-second{
      display: block;
    }
  }
}
.navPage-subMenu-item-child{
  .navPage-subMenu-action{
    &:hover{
      font-weight: 700;
    }
  }
}
.navPage-subMenu-item-second{
  &:hover{
    .navPage-subMenu{
  	   display: block;
    }
  }
}
.mobile-menu-nav{
  margin: 0; list-style: none;
  li{
    display: inline-block; vertical-align: middle;
    + li{
      margin-left: 15px;
    }
  }
}
.banners.home-top-banner{
  background: none;
  padding: 0;
  ul{
    margin: 0 -10px;
    li{
      float: left;
      width: 50%;
      padding: 0 10px;
      list-style: none;
    }
  }
}
.banners.home-bottom-banner{
  background: none;
  padding: 0;
  p{
    color: $color-textBase;
  }
}
.heroCarousel{
  .slick-dots{
    li{
      background: none;
      button{
        border-color: #000;
        &:hover{
          &:before{
            background: #000;
          }
        }
      }
      &.slick-active{
        button{
          &:before{
            background: #000;
          }
        }
      }
    }
  }
}
.newsletter-box{
  .form-field{
    margin-bottom: 0;
  }
}
.socialLinks{
  line-height: 21px;
}
.slick-dots{
  bottom: -45px;
}

.navPages-action {
	display: inline-block;
	padding: 0.78571rem 1.14286rem 0.78571rem;
}
.navPage-childList-action.navPages-action{
  padding: 0 0 0.35714rem;
}
.navPage-subMenu-action {
	padding: 0.78571rem 0 1.14286rem;
}
.footer{
  padding-bottom: 0;
}
.footer-bottom-bar{
  border-top: 1px solid #e5e5e5;
  padding: 10px 0;
}
.footer-payment-icons {
	margin-top: 0;
  .footer-payment-icon{
    margin: 0 0.35714rem;
  }
}
.footer-info-col--social{
  margin-top: 20px;
  .footer-info-heading{
    display: none;
  }
  .socialLinks{
    text-align: center;
  }
}

/* ====================== INNER PAGE STYLE ====================== */

ul.treeview{
  li{
    position: relative;
    float: none;
    width: 100%;
    display: block;
    ul{
      margin-left: 15px; display: none;margin-bottom: 0;
    }
  }
  .hitarea{
    height: 28px; width: 28px; position: absolute; top: 0; right: 0; background-image: $select-backgroundImage;
    background-position: center center;
    &.collapsable-hitarea{
      -ms-transform: rotate(180deg); -webkit-transform: rotate(180deg); transform: rotate(180deg);
    }
  }
}

.navList-treeview{
  li{
    position: relative;
    &:first-child{
      border-top: none;
    }
    a{
      padding: 10px 0;
    }
    .hitarea{
      position: absolute; width: 35px; height: 35px; text-align: center; line-height: 42px; right: 0; top: 0;
      cursor: pointer; z-index: 3; -webkit-transition: all 0.2s ease; transition: all 0.2s ease;
    }
    &.collapsable{
      > .hitarea{
        -ms-transform: rotate(180deg); -webkit-transform: rotate(180deg); transform: rotate(180deg);
        color: $color-textBase;
      }
      > a{
        color: $color-textBase;
      }
    }
    li{
      margin-top: 0;
      &:first-child{
        margin-top: 5px;
      }
      a{
        font-size: 14px;
      }
    }
    ul{
      display: none; margin-left: 10px;margin-bottom: 10px;
    }
  }
}
